// src/pages/TrendEdge.tsx
import React, { useEffect } from 'react';
import LightweightChart from '../components/LightweightChart';
import SetupFeed from '../components/SetupFeed';
import SetupReview from '../components/SetupReview';
import { logSetup, exportSetupLogCSV, getSetupLog } from '../alerts/setupLogger';

const TrendEdge: React.FC = () => {
  useEffect(() => {
    logSetup({
      symbol: 'NVDA',
      timeframe: '15m',
      source: 'ICC Swing Scanner',
      dashboard: 'TrendEdge',
      iccTags: ['Swing', 'TrendContinuation'],
      timeBlock: 'MidMorning',
      price: 482.1,
      outcome: 'Pending',
    });
  }, []);

  const handleExport = () => {
    const csv = exportSetupLogCSV();
    const blob = new Blob([csv], { type: 'text/csv;charset=utf-8;' });
    const url = URL.createObjectURL(blob);
    const link = document.createElement('a');
    link.href = url;
    link.setAttribute('download', 'trendedge_setups.csv');
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  const latestSetup = getSetupLog()
    .filter((s) => s.dashboard === 'TrendEdge')
    .slice(-1)[0];

  return (
    <div style={{ padding: '2rem' }}>
      <h2>ðŸ“ˆ TrendEdge â€” Swing Trades & Continuation Setups</h2>

      <div style={{ marginBottom: '1.5rem', overflowX: 'auto' }}>
        {latestSetup && (
          <LightweightChart
            symbol={latestSetup.symbol}
            timeframe={latestSetup.timeframe}
            overlays={['VWAP', 'SessionZones']}
            tags={latestSetup.iccTags}
            priceAtTrigger={latestSetup.price}
            outcome={latestSetup.outcome}
          />
        )}
      </div>

      <SetupFeed filterDashboard="TrendEdge" />
      <SetupReview filterDashboard="TrendEdge" />

      <button onClick={handleExport} style={{ marginTop: '1rem' }}>
        ðŸ“¤ Export TrendEdge Setups to CSV
      </button>
    </div>
  );
};

export default TrendEdge;
