// src/pages/MomentumEdge.tsx
import React, { useEffect } from 'react';
import LightweightChart from '../components/LightweightChart';
import SetupFeed from '../components/SetupFeed';
import SetupReview from '../components/SetupReview';
import SetupExport from '../components/SetupExport';
import { logSetup, getSetupLog } from '../alerts/setupLogger';
import type { ICCSetup } from '../types/ICC';

const MomentumEdge: React.FC = () => {
  useEffect(() => {
    logSetup({
      symbol: 'AMD',
      timeframe: '5m',
      source: 'ICC Momentum Scanner',
      dashboard: 'MomentumEdge',
      iccTags: ['Momentum', 'Breakout'],
      timeBlock: 'Open',
      priceAtTrigger: 124.5,
      outcome: 'Pending',
      bot: 'TestBot',
      timestamp: new Date().toISOString(),
    });
  }, []);

  const latestSetup: ICCSetup | undefined = getSetupLog()
    .filter(s => s.dashboard === 'MomentumEdge')
    .slice(-1)[0];

  return (
    <div style={{ padding: '2rem' }}>
      <h2>⚡ MomentumEdge — Momentum & Breakouts</h2>

      <div style={{ marginBottom: '1.5rem', overflowX: 'auto' }}>
        {latestSetup && (
          <LightweightChart
            data={[
              {
                time: latestSetup.timestamp,
                value: latestSetup.priceAtTrigger,
              },
            ]}
            overlays={{ vwap: true, triggers: [latestSetup] }}
          />
        )}
      </div>

      <SetupFeed
        entries={getSetupLog().filter(s => s.dashboard === 'MomentumEdge')}
        dashboard="MomentumEdge"
      />
      <SetupReview
        entries={getSetupLog().filter(s => s.dashboard === 'MomentumEdge')}
        dashboard="MomentumEdge"
      />
      <SetupExport dashboard="MomentumEdge" />
    </div>
  );
};

export default MomentumEdge;
