// src/LightweightChart.js
import React, { useEffect, useRef } from 'react';
import {
  createChart,
  CrosshairMode,
  LineStyle
} from 'lightweight-charts';

const LightweightChart = ({
  symbol = 'AAPL',
  timeframe = '5m',
  data = [],
  overlays = [],
  tags = [],
  priceAtTrigger = null,
  outcome = '',
  bot = false,
  timeBlock = '',
}) => {
  const chartContainerRef = useRef();

  useEffect(() => {
    const chart = createChart(chartContainerRef.current, {
      width: chartContainerRef.current.clientWidth || 800,
      height: 400,
      layout: {
        backgroundColor: '#0e0e0e',
        textColor: '#ccc',
      },
      grid: {
        vertLines: { color: 'transparent' },
        horLines: { color: 'transparent' },
      },
      crosshair: {
        mode: CrosshairMode.Normal,
      },
      timeScale: {
        borderColor: '#444',
      },
    });

    const series = chart.addLineSeries({
      color: '#00bfff',
      lineWidth: 2,
      lineStyle: LineStyle.Solid,
    });

    const safeData = Array.isArray(data) ? data : [];
    if (safeData.length > 0) {
      series.setData(safeData);
    }

    if (tags.length > 0) {
      console.log(`Chart tags: ${tags.join(', ')}`);
    }

    if (priceAtTrigger) {
      series.setMarkers([
        {
          time: safeData[0]?.time || '2025-11-11',
          position: 'aboveBar',
          color: outcome === 'Scored' ? 'green' : 'orange',
          shape: 'arrowDown',
          text: `Trigger @ ${priceAtTrigger}`,
        },
      ]);
    }

    // ðŸ•’ Dynamic session zone shading
    const sessionColorMap = {
      PreMarket: '#f5e9c5',
      AfterHours: '#cce6ff',
    };
    const sessionColor = sessionColorMap[timeBlock] || null;

    if (sessionColor) {
      chart.addLineSeries({
        color: sessionColor,
        lineWidth: 1,
        priceLineVisible: false,
      }).setData([
        {
          time: safeData[0]?.time || '2025-11-11',
          value: priceAtTrigger || 100,
        },
      ]);
    }

    // ðŸ“Š VWAP overlay (simulated)
    if (overlays.includes('VWAP')) {
      const vwapSeries = chart.addLineSeries({
        color: '#ffcc00',
        lineWidth: 1,
        lineStyle: LineStyle.Dotted,
      });

      const vwapData = safeData.map((point) => ({
        time: point.time,
        value: point.value * 0.98,
      }));

      vwapSeries.setData(vwapData);
    }

    const handleResize = () => {
      chart.resize(chartContainerRef.current.clientWidth || 800, 400);
    };
    window.addEventListener('resize', handleResize);

    return () => {
      window.removeEventListener('resize', handleResize);
      chart.remove();
    };
  }, [symbol, timeframe, data, overlays, tags, priceAtTrigger, outcome, bot, timeBlock]);

  return (
    <div>
      <h3 style={{ color: '#ccc', marginBottom: '0.5rem' }}>
        {symbol} â€” {timeframe} Chart {bot ? '(Bot)' : ''}
      </h3>
      <div
        ref={chartContainerRef}
        style={{
          width: '100%',
          height: '400px',
          padding: '1rem',
          boxSizing: 'border-box',
          overflow: 'hidden',
          border: '1px solid #333',
          borderRadius: '6px',
        }}
      />
    </div>
  );
};

export default LightweightChart;
