import type { ICCSetup } from '../types/ICC';

/**
 * Generate tags for external systems (e.g., TraderVue).
 */
export const generateTags = (setup: ICCSetup): string[] => {
  const tags: string[] = [];

  if (setup.dashboard === 'WarriorEdge') {
    tags.push('scalp');
  }
  if (setup.dashboard === 'MomentumEdge') {
    tags.push('momentum');
  }
  if (setup.dashboard === 'TrendEdge') {
    tags.push('swing');
  }

  // Add ICC tags directly
  if (setup.iccTags) {
    tags.push(...setup.iccTags);
  }

  return tags;
};
