// src/alerts/tagGenerator.js

/**
 * Generates a TraderVue-compatible tag string from ICC setup metadata.
 * Tags include ICC logic, source, dashboard, time block, and outcome.
 */
export const generateTags = (setup) => {
  const tags = [];

  // ICC logic tags
  if (setup.iccTags && setup.iccTags.length > 0) {
    setup.iccTags.forEach(tag => tags.push(`icc:${tag}`));
  }

  // Source of setup (e.g., scanner, bot, feed)
  if (setup.source) {
    tags.push(`source:${setup.source}`);
  }

  // Dashboard identifier (e.g., MomentumEdge, WarriorEdge)
  if (setup.dashboard) {
    tags.push(`dash:${setup.dashboard}`);
  }

  // Time block (e.g., PreMarket, MidMorning, OpenDrive)
  if (setup.timeBlock) {
    tags.push(`time:${setup.timeBlock}`);
  }

  // Outcome status (e.g., Pending, Scored, Missed)
  if (setup.outcome) {
    tags.push(`outcome:${setup.outcome}`);
  }

  return tags.join(' ');
};

/**
 * Returns a session tag based on timestamp.
 * Used for PreMarket, Regular, and AfterHours classification.
 */
export const getSessionTag = (timestamp) => {
  const hour = new Date(timestamp).getHours();
  if (hour < 9) return 'PreMarket';
  if (hour < 16) return 'Regular';
  return 'AfterHours';
};
