// src/pages/TrendEdge.tsx
import React, { useEffect } from 'react';
import LightweightChart from '../components/LightweightChart';
import SetupFeed from '../components/SetupFeed';
import SetupReview from '../components/SetupReview';
import SetupExport from '../components/SetupExport';
import { logSetup, getSetupLog } from '../alerts/setupLogger';
import type { ICCSetup } from '../types/ICC';

const TrendEdge: React.FC = () => {
  useEffect(() => {
    logSetup({
      symbol: 'NVDA',
      timeframe: '15m',
      source: 'ICC Swing Scanner',
      dashboard: 'TrendEdge',
      iccTags: ['Swing', 'TrendContinuation'],
      timeBlock: 'MidMorning',
      priceAtTrigger: 482.1,
      outcome: 'Pending',
      bot: 'TestBot',
      timestamp: new Date().toISOString(),
    });
  }, []);

  const latestSetup: ICCSetup | undefined = getSetupLog()
    .filter(s => s.dashboard === 'TrendEdge')
    .slice(-1)[0];

  return (
    <div style={{ padding: '2rem' }}>
      <h2>ðŸ“ˆ TrendEdge â€” Swing Trades & Continuation Setups</h2>

      <div style={{ marginBottom: '1.5rem', overflowX: 'auto' }}>
        {latestSetup && (
          <LightweightChart
            data={[
              {
                time: latestSetup.timestamp,
                value: latestSetup.priceAtTrigger,
              },
            ]}
            overlays={{ vwap: true, sessionZones: true, triggers: [latestSetup] }}
          />
        )}
      </div>

      <SetupFeed
        entries={getSetupLog().filter(s => s.dashboard === 'TrendEdge')}
        dashboard="TrendEdge"
      />
      <SetupReview
        entries={getSetupLog().filter(s => s.dashboard === 'TrendEdge')}
        dashboard="TrendEdge"
      />
      <SetupExport dashboard="TrendEdge" />
    </div>
  );
};

export default TrendEdge;
