import React, { useEffect, useRef } from 'react';
import {
  createChart,
  CrosshairMode,
  LineStyle
} from 'lightweight-charts';

const LightweightChart = ({
  symbol = 'AAPL',
  timeframe = '5m',
  data = [],
  overlays = [],
  tags = [],
  priceAtTrigger = null,
  outcome = '',
  bot = false,
}) => {
  const chartContainerRef = useRef();

  useEffect(() => {
    const chart = createChart(chartContainerRef.current, {
      width: chartContainerRef.current.clientWidth || 800,
      height: 400,
      layout: {
        backgroundColor: '#0e0e0e',
        textColor: '#ccc',
      },
      grid: {
        vertLines: { color: 'transparent' },
        horLines: { color: 'transparent' },
      },
      crosshair: {
        mode: CrosshairMode.Normal,
      },
      timeScale: {
        borderColor: '#444',
      },
    });

    const series = chart.addLineSeries({
      color: '#00bfff',
      lineWidth: 2,
      lineStyle: LineStyle.Solid,
    });

    // âœ… Safe fallback for missing or undefined data
    const safeData = Array.isArray(data) ? data : [];
    if (safeData.length > 0) {
      series.setData(safeData);
    }

    // ðŸ”§ Placeholder: ChartEye overlays
    // TODO: Import and apply ICC logic here (e.g., session zones, VWAP, reversal markers)

    // ðŸ”§ Placeholder: TrendSpider zones
    // TODO: Overlay dynamic zones once API is integrated (e.g., supply/demand, anchored VWAP)

    // ðŸ·ï¸ Tag awareness
    if (tags.length > 0) {
      console.log(`Chart tags: ${tags.join(', ')}`);
    }

    // ðŸŽ¯ ICC Trigger Marker
    if (priceAtTrigger) {
      series.setMarkers([
        {
          time: safeData[0]?.time || '2025-11-11',
          position: 'aboveBar',
          color: outcome === 'Scored' ? 'green' : 'orange',
          shape: 'arrowDown',
          text: `Trigger @ ${priceAtTrigger}`,
        },
      ]);
    }

    // ðŸ•’ Session zone shading (soft gold, blue)
    const sessionShading = [
      { time: '2025-11-11', color: '#f5e9c5', label: 'PreMarket' },
      { time: '2025-11-11', color: '#cce6ff', label: 'AfterHours' },
    ];

    sessionShading.forEach((zone) => {
      chart.addLineSeries({
        color: zone.color,
        lineWidth: 1,
        priceLineVisible: false,
      }).setData([
        {
          time: zone.time,
          value: priceAtTrigger || 100,
        },
      ]);
    });

    // ðŸ“Š VWAP overlay (simulated)
    if (overlays.includes('VWAP')) {
      const vwapSeries = chart.addLineSeries({
        color: '#ffcc00',
        lineWidth: 1,
        lineStyle: LineStyle.Dotted,
      });

      const vwapData = safeData.map((point) => ({
        time: point.time,
        value: point.value * 0.98, // mock VWAP ~2% below price
      }));

      vwapSeries.setData(vwapData);
    }

    // ðŸ–¥ï¸ Responsive resize
    const handleResize = () => {
      chart.resize(chartContainerRef.current.clientWidth || 800, 400);
    };
    window.addEventListener('resize', handleResize);

    return () => {
      window.removeEventListener('resize', handleResize);
      chart.remove();
    };
  }, [symbol, timeframe, data, overlays, tags, priceAtTrigger, outcome, bot]);

  return (
    <div>
      <h3 style={{ color: '#ccc', marginBottom: '0.5rem' }}>
        {symbol} â€” {timeframe} Chart {bot ? '(Bot)' : ''}
      </h3>
      <div
        ref={chartContainerRef}
        style={{
          width: '100%',
          height: '400px',
          padding: '1rem',
          boxSizing: 'border-box',
          overflow: 'hidden',
          border: '1px solid #333',
          borderRadius: '6px',
        }}
      />
    </div>
  );
};

export default LightweightChart;
