import React, { useEffect } from 'react';
import LightweightChart from '../LightweightChart';
import SetupFeed from '../components/setupFeed';
import SetupReview from '../components/setupReview';
import { logSetup, exportSetupLogCSV, getSetupLog } from '../alerts/setupLogger';
import { injectLiveSetup } from '../alerts/scannerFeed';

const MomentumEdge = () => {
  // ðŸ”§ Inject mock ICC setup + simulate live bot feed
  useEffect(() => {
    // Static setup for initial load
    logSetup({
      symbol: 'TSLA',
      timeframe: '5m',
      source: 'ICC Momentum Scanner',
      dashboard: 'MomentumEdge',
      iccTags: ['Breakout', 'TrendContinuation'],
      timeBlock: 'MidMorning',
      priceAtTrigger: 238.90,
      outcome: 'Pending',
    });

    // Simulated bot feed injection after delay
    const mockLiveSetup = {
      symbol: 'AMD',
      timeframe: '5m',
      source: 'ICC Momentum Bot',
      dashboard: 'MomentumEdge',
      iccTags: ['Breakout', 'Momentum'],
      timeBlock: 'LateMorning',
      priceAtTrigger: 112.45,
    };

    const timer = setTimeout(() => injectLiveSetup(mockLiveSetup), 1500);
    return () => clearTimeout(timer);
  }, []);

  // ðŸ“¤ Export setups to CSV
  const handleExport = () => {
    const csv = exportSetupLogCSV();
    const blob = new Blob([csv], { type: 'text/csv;charset=utf-8;' });
    const url = URL.createObjectURL(blob);
    const link = document.createElement('a');
    link.href = url;
    link.setAttribute('download', 'momentumedge_setups.csv');
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  // ðŸ§  Get latest setup for chart injection
  const latestSetup = getSetupLog()
    .filter((s) => s.dashboard === 'MomentumEdge')
    .slice(-1)[0];

  return (
    <div style={{ padding: '2rem' }}>
      <h2>ðŸš€ MomentumEdge â€” Breakouts & Trend Continuation</h2>

      {/* Chart container */}
      <div style={{ marginBottom: '1.5rem', overflowX: 'auto' }}>
        {latestSetup && (
          <LightweightChart
            symbol={latestSetup.symbol}
            timeframe={latestSetup.timeframe}
            overlays={['VWAP', 'SessionZones']}
            tags={latestSetup.iccTags}
            priceAtTrigger={latestSetup.priceAtTrigger}
            outcome={latestSetup.outcome}
          />
        )}
      </div>

      {/* ICC Setup Feed */}
      <div style={{ marginBottom: '1.5rem' }}>
        <SetupFeed filterDashboard="MomentumEdge" />
      </div>

      {/* Setup Review */}
      <div style={{ marginBottom: '1.5rem' }}>
        <SetupReview filterDashboard="MomentumEdge" />
      </div>

      {/* CSV Export Button */}
      <button onClick={handleExport} style={{ marginTop: '1rem' }}>
        ðŸ“¤ Export MomentumEdge Setups to CSV
      </button>
    </div>
  );
};

export default MomentumEdge;
