// src/components/LightweightChart.tsx
import React, { useEffect, useRef } from 'react';
import { createChart, IChartApi, LineData } from 'lightweight-charts';
import type { ICCSetup } from '../types/ICC';

interface LightweightChartProps {
  data: { time: string; value: number }[]; // normalized to yyyy-mm-dd string
  overlays?: {
    vwap?: boolean;
    sessionZones?: boolean;
    triggers?: ICCSetup[];
  };
}

const LightweightChart: React.FC<LightweightChartProps> = ({ data, overlays }) => {
  const chartContainerRef = useRef<HTMLDivElement>(null);
  const chartRef = useRef<IChartApi | null>(null);

  useEffect(() => {
    if (!chartContainerRef.current) return;

    // Destroy previous chart if exists
    if (chartRef.current) {
      chartRef.current.remove();
    }

    const chart = createChart(chartContainerRef.current, {
      width: chartContainerRef.current.clientWidth,
      height: 400,
      layout: { background: { color: '#ffffff' }, textColor: '#000' },
      grid: { vertLines: { color: '#eee' }, horzLines: { color: '#eee' } },
    });

    const lineSeries = chart.addLineSeries();

    // Normalize data to LineData format
    const formattedData: LineData[] = data.map(d => ({
      time: d.time, // must be yyyy-mm-dd string
      value: d.value,
    }));
    lineSeries.setData(formattedData);

    // Overlays
    if (overlays?.vwap) {
      // VWAP overlay logic placeholder
    }
    if (overlays?.sessionZones) {
      // Session zone shading logic placeholder
    }
    if (overlays?.triggers) {
      lineSeries.setMarkers(
        overlays.triggers.map(trigger => ({
          time: new Date(trigger.timeBlock).toISOString().slice(0, 10),
          position: 'aboveBar',
          color: 'red',
          shape: 'arrowDown',
          text: trigger.iccTags.join(','),
        }))
      );
    }

    chartRef.current = chart;
    return () => chart.remove();
  }, [data, overlays]);

  return <div ref={chartContainerRef} style={{ width: '100%', height: '400px' }} />;
};

export default LightweightChart;
