// src/pages/DefaultDashboard.tsx
import React from 'react';
import SetupFeed from '../components/SetupFeed';
import SetupReview from '../components/SetupReview';
import LightweightChart from '../components/LightweightChart';
import SetupExport from '../components/SetupExport';   // âœ… reusable export button
import { getSetupLog } from '../alerts/setupLogger';
import type { ICCSetup } from '../types/ICC';

const DefaultDashboard: React.FC = () => {
  // Aggregate all setups across dashboards
  const allSetups: ICCSetup[] = getSetupLog();

  // Get latest setup across all dashboards for chart preview
  const latestSetup: ICCSetup | undefined = allSetups.slice(-1)[0];

  return (
    <div style={{ padding: '2rem' }}>
      <h1>ðŸ“Š Default Dashboard â€” ICC Summary</h1>
      <p>
        This view aggregates setups from <strong>MomentumEdge</strong>, <strong>TrendEdge</strong>, and <strong>WarriorEdge</strong>.
        Use this dashboard for a consolidated feed, review, and export.
      </p>

      <div style={{ marginBottom: '1.5rem', overflowX: 'auto' }}>
        {latestSetup && (
          <LightweightChart
            data={[
              {
                time: new Date(latestSetup.timeBlock).toISOString().slice(0, 10),
                value: latestSetup.priceAtTrigger,
              },
            ]}
            overlays={{ vwap: true, sessionZones: true, triggers: [latestSetup] }}
          />
        )}
      </div>

      <SetupFeed entries={allSetups} dashboard="All Dashboards" />
      <SetupReview entries={allSetups} dashboard="All Dashboards" />

      {/* âœ… Reusable export button for all dashboards */}
      <SetupExport />
    </div>
  );
};

export default DefaultDashboard;
