// src/components/LightweightChart.tsx
import React, { useEffect, useRef } from 'react';
import { createChart, ColorType } from 'lightweight-charts';

interface LightweightChartProps {
  symbol: string;
  timeframe: string;
  overlays?: string[];
  tags?: string[];
  priceAtTrigger?: number;
  outcome?: string;
}

const LightweightChart: React.FC<LightweightChartProps> = ({
  symbol,
  timeframe,
  overlays = [],
  tags = [],
  priceAtTrigger,
  outcome,
}) => {
  const ref = useRef<HTMLDivElement>(null);

  useEffect(() => {
    if (!ref.current) return;

    // ✅ Removed "type: 'solid'" — just use color
    const chart = createChart(ref.current, {
      height: 300,
      layout: {
        background: { color: '#0b0e14' },
        textColor: '#d0d6e2',
      },
    });

    // Example candlestick series
    const candleSeries = chart.addCandlestickSeries();
    candleSeries.setData([
      { time: '2023-11-13', open: 480, high: 490, low: 475, close: 485 },
      { time: '2023-11-14', open: 485, high: 495, low: 480, close: 492 },
    ]);

    // Example overlay (VWAP, etc.)
    if (overlays.includes('VWAP')) {
      const lineSeries = chart.addLineSeries({ color: 'orange' });
      lineSeries.setData([
        { time: '2023-11-13', value: 482 },
        { time: '2023-11-14', value: 488 },
      ]);
    }

    return () => chart.remove();
  }, [symbol, timeframe, overlays]);

  return (
    <div>
      <h4>
        {symbol} ({timeframe}) — {outcome ?? 'Pending'}
      </h4>
      {priceAtTrigger && <p>Triggered at: ${priceAtTrigger}</p>}
      {tags.length > 0 && <p>Tags: {tags.join(', ')}</p>}
      <div ref={ref} />
    </div>
  );
};

export default LightweightChart;
